mapper.update:
update tb_course
set course_description = #{courseDescription,jdbcType=VARCHAR},
  course_teacher = #{courseTeacher,jdbcType=INTEGER},
  course_type = #{courseType,jdbcType=VARCHAR},
  course_organization_id = #{courseOrganizationId,jdbcType=INTEGER}
where course_id = #{courseId,jdbcType=INTEGER}

mapper.select:
select course_id, course_description, course_teacher, course_type, course_organization_id
from tb_course
where course_id = #{courseId,jdbcType=INTEGER}
