mapper.update:
update tb_grade
set grade_course_id = #{gradeCourseId,jdbcType=INTEGER},
  grade_student_id = #{gradeStudentId,jdbcType=INTEGER},
  grade_number = #{gradeNumber,jdbcType=INTEGER}
where grade_id = #{gradeId,jdbcType=INTEGER}

mapper.select:
select grade_id, grade_course_id, grade_student_id, grade_number
from tb_grade
where grade_id = #{gradeId,jdbcType=INTEGER}
