mapper.update:
update tb_organization_student
set organization_id = #{organizationId,jdbcType=INTEGER},
  student_id = #{studentId,jdbcType=INTEGER}
where organization_student_id = #{organizationStudentId,jdbcType=INTEGER}

mapper.select:
select organization_student_id, organization_id, student_id
from tb_organization_student
where organization_student_id = #{organizationStudentId,jdbcType=INTEGER}
